module.exports = {
  remove: function(item){
    var index = this.indexOf(item)
    if(index > -1){
      this.splice(index, 1)
      return true
    }
    return false
  },

  has: function(item){
    return this.indexOf(item) >- 1
  },

  count: function(condition) {
    var c = 0
    for (var i = 0; i < this.length; i++) {
      if (condition(this[i])) {
        c++
      }
    }
    return c
  },

  find: function(condition) {
    for (var i = 0; i < this.length; i++) {
      if (condition(this[i])) {
        return this[i]
      }
    }
  },

  best: function(compare) {
    var best = this[0]
    for (var i = 1; i < this.length; i++) {
      var item = this[i]
      if (
          (typeof compare === 'function' && compare(item, best)) ||
          (typeof compare === 'string' && item[compare] > best[compare])
        ) {
        best = item
      }
    }
    return best
  }
}
